mapper.select:
select count(1)
from BX_COMMENT c
where c.RESPONDENT_ID=#{id}

    and c.`STATUS`=#{status}

select c.id,c.role,c.COMMENTATOR_WECHAT,c.COMMENTATOR_IMG,c.COMMENTATOR_NAME,
c.COMMENT_CONTEXT,DATE_FORMAT(c.CREATE_DATE,'%Y-%m-%d %H:%i:%s') CREATE_DATE,c.`STATUS`
from BX_COMMENT c
where c.RESPONDENT_ID=#{id}

    and c.`STATUS`=#{status}

order by c.CREATE_DATE desc
LIMIT #{skip},#{pageSize}
